import React, { useState,useEffect } from 'react';
import Helpers from '../../Config/Helpers';
import Sidebar from '../../Components/Sidebar';
import axios from "axios";

const Location = () => {
    const [location, setLocation] = useState('');
    const [locations, setLocations] = useState([]);
     // Fetch locations
 useEffect(() => {
    const fetchLocations = async () => {
      try {
        const url = Helpers.apiUrl;
        const token = localStorage.getItem("token");
        const response = await axios.get(`${url}fetch-locations`, {
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        });
  
        if (response.status === 200) {
          console.log("API locations:", response.data.data);
          setLocations(response.data.data);
        } else {
          console.log("Received non-200 response:", response.status);
        }
      } catch (error) {
        console.error("Error fetching location data:", error);
      }
    };
  
    fetchLocations();
  }, []);
    const handleSubmit = async () => {
        if (!location.trim()) {
            Helpers.toast("error", "Please enter a location");
            return;
        }
        const baseUrl = Helpers.apiUrl;
        const token = localStorage.getItem("token");
        const response = await fetch(`${baseUrl}add-location`, {
            method: 'POST',
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
                Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify({ location })
        });
        if (response.status === 200) {
            const data = await response.json();
            console.log(data);
            Helpers.toast('success', "Location Saved Successfully!");
            setLocation("");
        }
        else {
            Helpers.toast("error", "Error saving location. Please try again later.");
        }
    };

    return (
        <div className='container'>
        <div className="flex justify-center items-center h-screen bg-[#ebe9e9]">
            {/* <Sidebar /> */}
            {/* <div
                className="container bg-white p-8 rounded-lg shadow-lg"
                style={{
                    maxWidth: "400px"
                }}
            >
                <h2 className="text-2xl font-bold mb-4">Create Location</h2>
                <input
                    type="text"
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                    placeholder="Enter Location..."
                    className="w-full border rounded-md px-4 py-4 mb-4"
                />
                <button
                    onClick={handleSubmit}
                    className="w-full bg-[#E7737B] text-gray-200 font-bold py-2 px-4 rounded-md hover:bg-[#d4959a]"
                >
                    Submit
                </button>
            </div> */}
            <div className="col-6">
        <h2>Locations</h2>
        <table className="table">
          <thead>
            <tr>
            <th scope="col">#</th>
              <th scope="col">Location</th>
        
            </tr>
          </thead>
          <tbody>
            {locations.length > 0 ? (
              locations.map((location, index) => (
                <tr key={index}>
                  <th scope="row">{index + 1}</th>
                  <td>{location.location}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="3">No locations found.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
        </div>
        </div>
    );
};

export default Location;
