import { useEffect, useState } from "react";
import Sidebar from '../../Components/Sidebar'
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import Helpers from '../../Config/Helpers';

export default function TemplateList() {
    const navigate = useNavigate();
    const [isLoading, setIsLoading] = useState(false);
    const [templates, setTemplate] = useState(null);

    const handleUpdate = (templateId) => {
        navigate(`/admin/templates/update/${templateId}`);
    };

    useEffect(() => {
        const fetchData = async () => {
            setIsLoading(true);
            try {
                const baseUrl = Helpers.apiUrl;
                const token = localStorage.getItem("token");
                // const data = {}; // Your request payload
                // Retrieve token if needed
                // console.log("token", token);
                const response = await axios.get(`${baseUrl}admin/mail_templates`, {
                    headers: {
                        Accept: "application/json",
                        "Content-Type": "application/json",
                        Authorization: `Bearer ${token}`,
                    },
                });

                // console.log("response", response.data.data);

                if (response.status === 200) {
                    // console.log(response.data.user);
                    // Assuming you want to store the emails in state
                    setTemplate(response.data.data);

                    // Redirect or perform further actions
                    // console.log(response.data);
                } else {
                    console.log("Received non-200 response:", response.status);
                }
            } catch (error) {
                console.error("An error occurred:", error);
                // Handle error
                if (error.response) {
                    // Server responded with a status code outside 2xx range
                    console.error("Error data:", error.response.data);
                    console.error("Error status:", error.response.status);
                } else if (error.request) {
                    // No response was received to the request
                    console.error("No response received:", error.request);
                } else {
                    // An error occurred in setting up the request
                    console.error("Error message:", error.message);
                }
            } finally {
                setIsLoading(false);
            }
        };

        fetchData();
    }, []); // Empty dependency array means this effect runs once after the initial render
    const handleDelete = async (templateId) => {
        if (window.confirm("Are you sure you want to delete this template?")) {
            const baseUrl = Helpers.apiUrl;
            const token = localStorage.getItem("token");
            try {
                const response = await axios.delete(
                    `${baseUrl}admin/mail_templates/${templateId}`,
                    {
                        headers: {
                            Accept: "application/json",
                            "Content-Type": "application/json",
                            Authorization: `Bearer ${token}`,
                        },
                    }
                );

                if (response.status === 200) {
                    console.log("Template deleted successfully");
                    // Optionally, refresh your templates list here
                    window.location.reload();
                    // navigate('admin/dashboard')
                }
            } catch (error) {
                console.error("An error occurred during deletion:", error);
                // Handle the error appropriately
            }
        }
    };

    return (
        <div className="flex h-screen bg-[#ebe9e9]">
            <Sidebar />
            <div className="container bg-white p-8 shadow-lg">
                <div className="row w-full">
                    <div className="nk-block-head-content flex justify-between items-center w-full bg-gray-200 p-4">
                        {/* Left side: "Create Template" title */}
                        <div className="flex items-cente">
                            <h2 className="display-6">User's Template</h2>
                        </div>

                        {/* Right side: "Back" button */}
                        <div >
                        <Link to="/user/newtemplate" className="btn" style={{background:"#E2545E",color:"white"}}>
                        Add New
                      </Link>
                            {/* <button onClick={() => window.history.back()} className="btn bg-[#E2545E] hover:bg-[#df7980] py-2 px-3 text-gray-600 hover:text-gray-900 focus:outline-none">
                                Create New
                            </button> */}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
