import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Helpers from '../../Config/Helpers';

export default function UpdateTemplate() {
    const { templateId } = useParams();
    const navigate = useNavigate();
    const [templateDetails, setTemplateDetails] = useState({
        heading: "",
        template_name: "",
    });
    useEffect(() => {
        const fetchTemplateDetails = async () => {
            const baseUrl = Helpers.apiUrl;
            const token = localStorage.getItem("token");
            try {
                const response = await axios.get(`${baseUrl}admin/mail_templates/${templateId}`, {
                    headers: {
                        Accept: "application/json",
                        "Content-Type": "application/json",
                        Authorization: `Bearer ${token}`,
                    },
                });
                if (response.data && response.data.data) {
                    const { data } = response.data;
                    setTemplateDetails({
                        heading: data.heading || "",
                        template_name: data.template_name || "",
                    });
                }
            } catch (error) {
                console.error("Failed to fetch template details", error);
            }
        };
  
        fetchTemplateDetails();
    }, [templateId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setTemplateDetails(prevState => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const baseUrl = Helpers.apiUrl;
        const token = localStorage.getItem("token");
        try {
            const response = await axios.put(`${baseUrl}admin/mail_templates/${templateId}`, templateDetails, {
                headers: {
                    Accept: "application/json",
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${token}`,
                },
            });
            if (response.status === 200 || response.status === 204) {
                Helpers.toast("success", 'Template updated successfully');
                navigate('/admin/dashboard');
            }
        } catch (error) {
            console.error("Failed to update template", error);
        }
    };

    const insertTag = (tag) => {
        setTemplateDetails(prevState => ({
            ...prevState,
            heading: `${prevState.heading}[${tag}]`,
        }));
    };

  return (
    <div className="flex h-screen bg-[#ebe9e9]">
    <Sidebar />
    <div className="container bg-white p-8 shadow-lg">
        <div className="row w-full">
            <div className="nk-block-head-content flex justify-between items-center w-full p-4">
                <div className="flex items-cente">
                    <h2 className="display-6">User's Template</h2>
                </div>

                <div >
                <Link to="/user/create-template" className="btn btn-sm" style={{background:"#E2545E",color:"white"}}>
                Add New
              </Link>
                   
                </div>
                
            </div>
        </div>
        <div className="nk-block">
          <div className="card">
          
          </div>
        </div>
    </div>
</div>
  )
}
