import React, { useState, useEffect } from 'react';
import Helpers from '../../Config/Helpers';
import Sidebar from '../../Components/Sidebar';
import axios from "axios";

const Location = () => {
  const [location, setLocation] = useState('');
  const [locations, setLocations] = useState([]);

  // Fetch locations
  useEffect(() => {
    const fetchLocations = async () => {
      try {
        const url = Helpers.apiUrl;
        const token = localStorage.getItem("token");
        const response = await axios.get(`${url}fetch-locations`, {
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        });

        if (response.status === 200) {
          console.log("API locations:", response.data.data);
          setLocations(response.data.data);
        } else {
          console.log("Received non-200 response:", response.status);
        }
      } catch (error) {
        console.error("Error fetching location data:", error);
      }
    };

    fetchLocations();
  }, []);

  const handleSubmit = async () => {
    if (!location.trim()) {
      Helpers.toast("error", "Please enter a location");
      return;
    }

    const baseUrl = Helpers.apiUrl;
    const token = localStorage.getItem("token");
    const response = await fetch(`${baseUrl}add-location`, {
      method: 'POST',
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ location }),
    });

    if (response.status === 200) {
      const data = await response.json();
      console.log(data);
      Helpers.toast('success', "Location Saved Successfully!");
      setLocation("");
    } else {
      Helpers.toast("error", "Error saving location. Please try again later.");
    }
  };

  return (
    <div className="flex h-screen bg-gray-200">
      <Sidebar />

      <div className="container mx-auto my-6 flex flex-col md:flex-row gap-8 p-4 bg-white shadow-lg rounded-lg">
        {/* Form Section */}
        <div className="flex-1 my-6 ">
          <h2 className="text-3xl font-bold mb-4">Create Location</h2>
          <input
            type="text"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            placeholder="Enter Location..."
            className="w-full border border-gray-300 rounded-md px-4 py-2 mb-4 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            onClick={handleSubmit}
            className="w-full bg-blue-500 text-white font-bold py-2 rounded-md hover:bg-blue-600 transition-colors"
          >
            Submit
          </button>
        </div>

        {/* Locations Table Section */}
        <div className="flex-1">
          <h2 className="text-3xl font-bold mb-4">Locations</h2>
          <table className="table-auto w-full border border-gray-300">
            <thead>
              <tr className="bg-blue-100">
                <th className="border px-4 py-2">#</th>
                <th className="border px-4 py-2">Location</th>
              </tr>
            </thead>
            <tbody>
              {locations.length > 0 ? (
                locations.map((loc, index) => (
                  <tr key={index} className="hover:bg-gray-100">
                    <th className="border px-4 py-2">{index + 1}</th>
                    <td className="border px-4 py-2">{loc.location}</td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td className="border px-4 py-2" colSpan="2">
                    No locations found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Location;
