import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserModel = () => {
    const [collections, setCollections] = useState([]);
    const [selectedCollection, setSelectedCollection] = useState('');
    const [productName, setProductName] = useState('');
    const [slug, setSlug] = useState('');
    const [image, setImage] = useState(null);

    const fetchCollections = async () => {
        try {
            // Replace this with your actual endpoint
            const response = await axios.get('YOUR_API_URL_TO_FETCH_COLLECTIONS');
            if (response.status === 200) {
                setCollections(response.data.collections || []);
            } else {
                setCollections([]); // Set empty if there's an issue
            }
        } catch (error) {
            console.error("Error fetching collections:", error);
            setCollections([]); // Error fallback
        }
    };

    useEffect(() => {
        fetchCollections();
    }, []);

    const handleSave = async () => {
        if (!selectedCollection || !productName || !slug || !image) {
            console.log("All fields are required");
            return;
        }

        const formData = new FormData();
        formData.append('collection_id', selectedCollection);
        formData.append('product_name', productName);
        formData.append('slug', slug);
        formData.append('image', image);

        try {
            // Replace with your actual API endpoint for saving the data
            const response = await axios.post('YOUR_API_URL_TO_SAVE_PRODUCT', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            if (response.status === 200) {
                console.log("Product saved successfully");
                setSelectedCollection('');
                setProductName('');
                setSlug('');
                setImage(null);
            } else {
                console.error("Failed to save product");
            }
        } catch (error) {
            console.error("Error saving product:", error);
        }
    };

    return (
        <div className='flex-1 m-7'>
            <h2 className='text-3xl font-bold mb-4'>Create Product</h2>
            <div className='flex flex-col space-y-4 mb-4'>
                {/* Collection Dropdown */}
                <select
                    value={selectedCollection}
                    onChange={(e) => setSelectedCollection(e.target.value)}
                    className='border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500'
                >
                    <option value=''>Select Collection...</option>
                    {collections.map((collection) => (
                        <option key={collection.id} value={collection.id}>
                            {collection.name}
                        </option>
                    ))}
                </select>
                {/* Product Name Input */}
                <input
                    type='text'
                    value={productName}
                    onChange={(e) => setProductName(e.target.value)}
                    placeholder='Enter Product Name...'
                    className='border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500'
                />
                {/* Slug Input */}
                <input
                    type='text'
                    value={slug}
                    onChange={(e) => setSlug(e.target.value)}
                    placeholder='Enter Slug...'
                    className='border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500'
                />
                {/* Image Upload Input */}
                <input
                    type='file'
                    onChange={(e) => setImage(e.target.files[0])}
                    className='border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500'
                />
                {/* Save Button */}
                <div className='w-150'>
                    <button
                        onClick={handleSave}
                        className='bg-[#f36b74] !text-white font-bold px-4 py-2 rounded-md hover:bg-[#fa9198] transition-colors'
                    >
                        Submit
                    </button>
                </div>
            </div>
        </div>
    );
};

export default UserModel;
