import React, { useState } from 'react';
import { toast } from 'react-toastify';
import Helpers from '../../Config/Helpers';
import Sidebar from '../../Components/Sidebar';

const Store = () => {
    const [storeName, setStoreName] = useState('');

    const handleSubmit = async () => {
        if (!storeName.trim()) {
            toast.error("Please enter a store name");
            return;
        }
        const baseUrl = Helpers.apiUrl;
        const token = localStorage.getItem("token");
        const response = await fetch(`${baseUrl}store`, {
            method: 'POST',
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
                Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify({ storeName })
        });
        const data = await response.json();
        console.log(data); // Process the response or show a message
    };

    return (
        <div className="flex justify-center items-center h-screen bg-[#ebe9e9]">
            <Sidebar />
            <div
                className="container bg-white p-8 rounded-lg shadow-lg"
                style={{
                    maxWidth: "400px"
                }}
            >
                <h2 className="text-2xl font-bold mb-4">Enter Store Name</h2>
                <input
                    type="text"
                    value={storeName}
                    onChange={(e) => setStoreName(e.target.value)}
                    placeholder="Store Name"
                    className="w-full border rounded-md px-4 py-2 mb-4"
                />
                <button
                    onClick={handleSubmit}
                    className="w-full bg-blue-500 text-white font-bold py-2 px-4 rounded-md hover:bg-blue-600"
                >
                    Submit
                </button>
            </div>
        </div>
    );
};

export default Store;
