import React, { useState, useRef } from "react";
import axios from "axios";
import Helpers from "../../Config/Helpers";
import { useNavigate } from "react-router-dom";
import Sidebar from "../../Components/Sidebar";

function CreateTemplate() {
    const [greetings, setGreetings] = useState("");
    const [template_name, setName] = useState("");
    const [heading, setHeading] = useState("");
    const [footer, setFooter] = useState("");
    const [links, setLinks] = useState("");
    const navigate = useNavigate();
    const textareaRef = useRef(null); // Create a ref for the textarea

    const handleSubmit = async (e) => {
        e.preventdefault();
        if (!template_name.trim() || !heading.trim()) {
            Helpers.toast("error", "Please fill in all required fields.");
            return; // Stop the function if validation fails
        }

        const formData = {
            greetings,
            heading,
            template_name,
            footer,
            links,
        };

        const baseurl = Helpers.apiurl;
        const token = localstorage.getitem("token");
        try {
            const response = await axios.post(
                `${baseurl}admin/mail_templates`,
                formData,
                {
                    headers: {
                        accept: "application/json",
                        "content-type": "application/json",
                        authorization: `bearer ${token}`,
                    },
                }
            );

            // Check if the status code is within the 2xx success range
            if (response.status >= 200 && response.status < 300) {
                Helpers.toast("success", "Template Data Stored Successfully");
                setgreetings("");
                setname("");
                setheading("");
                setfooter("");
                setlinks("");
                window.history.back();
            } else {
                Helpers.toast("error", "Failed to store data, please try again.");
                console.log("Received non-2xx response:", response.status);
            }
        } catch (error) {
            console.error("An error occurred:", error);
            Helpers.toast("error", error.response?.data?.message || "An unexpected error occurred.");
        }
    };

    const inserttag = (tag) => {
        if (textarearef.current) {
            const textarea = textarearef.current;
            const startpos = textarea.selectionstart;
            const endpos = textarea.selectionend;
            const newheading =
                heading.slice(0, startpos) + `[${tag}]` + heading.slice(endpos);
            setheading(newheading);
            // Set cursor position after inserted tag
            settimeout(() => {
                textarea.setselectionrange(startpos + `[${tag}]`.length, startpos + `[${tag}]`.length);
            }, 0);
        }
    };

    return (
        <>
            {/* <Sidebar /> */}
            <div className="flex  bg-[#ebe9e9]">
                <Sidebar />
                <div className="container bg-white p-8 shadow-lg">
                    <div className="shadow-lg p-3">
                        <div className="nk-content">
                            <div className="nk-content-inner">
                                <div className="nk-content-body">
                                    <div className="nk-block-head nk-page-head">
                                        <div classname="nk-block-head-between">
                                            <div classname="row w-full">
                                                <div className="nk-block-head-content flex justify-between items-center w-full bg-gray-200 p-4">
                                                    <div classname="flex items-cente">
                                                        <h2 className="display-6">Create Template</h2>
                                                    </div>
                                                    <div>
                                                        <button
                                                            onClick={() => window.history.back()}
                                                            className="btn bg-[#E2545E] hover:bg-[#df7980] py-2 px-3 text-gray-600 hover:text-gray-900 focus:outline-none">
                                                            Back
                                                        </button>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="card shadown-none col-md-8">
                                            <div className="card-body">
                                                <div className="row g-3 gx-gs">
                                                    <div className="col-md-12">
                                                        <div className="form-group">
                                                            <label htmlFor="exampleFormControlInputText2" className="form-label">Add Title</label>
                                                            <div className="form-control-wrap">
                                                                <input
                                                                    type="text"
                                                                    className="form-control"
                                                                    id="exampleFormControlInputText2"
                                                                    placeholder="Enter Name"
                                                                    name="template_name"
                                                                    value={template_name}
                                                                    onChange={(e) => setName(e.target.value)}
                                                                />
                                                            </div>
                                                        </div>
                                                    </div>
                                                    <div className="mb-2">
                                                        <button onClick={() => insertTag("MyName")} className="btn btn-secondary m-1 flex-1 py-2">My Name</button>
                                                        <button onClick={() => insertTag("ClientName")} className="btn btn-secondary m-1 flex-1 py-2">Client Name</button>
                                                        <button onClick={() => insertTag("MyEmail")} className="btn btn-secondary m-1 flex-1 py-2">My Email</button>
                                                        <button onClick={() => insertTag("MyPhone")} className="btn btn-secondary m-1 flex-1 py-2">My Phone</button>
                                                        <button onClick={() => insertTag("Address")} className="btn btn-secondary m-1 flex-1 py-2">Store Address</button>
                                                        {/* <button onClick(()enshoesag("MyEmail")} className="btn btn-secondary m-1 flex-1 py-2">My Email</button> */}
                                                        {/* <button onClick(()shoestag("MyPhone")} className="btn btn-secondary m-1 flex-1 py-2">My Phone</button>
                                                        <button onClick(()enshoesag("Address")} classname="btn btn-secondary m-1 flex-1 py-2">Store Address</button> */}
                                                        <button onClick={() => insertTag("Collection")} className="btn btn-secondary m-1 flex-1 py-2">Collection Name</button>
                                                        <button onClick={() => insertTag("Model")} className="btn btn-secondary m-1 flex-1 py-2">Model Name</button>
                                                        <button onClick={() => insertTag("Link")} className="btn btn-secondary m-1 flex-1 py-2">Link</button>
                                                        <button onClick={() => insertTag("Image")} className="btn btn-secondary m-1 flex-1 py-2">Image</button>
                                                        <button onClick={() => insertTag("Features")} className="btn btn-secondary m-1 flex-1 py-2">Features</button>
                                                        <button onClick={() => insertTag("Benefits")} className="btn btn-secondary m-1 flex-1 py-2">Benefits</button>
                                                        <button onClick={() => insertTag("Store")} className="btn btn-secondary m-1 flex-1 py-2">Store</button>
                                                        <button onClick={() => insertTag("Location")} className="btn btn-secondary m-1 flex-1 py-2">Location</button>
                                                        <button onClick={() => insertTag("Price")} className="btn btn-secondary m-1 flex-1 py-2">Price</button>
                                                        <button onClick={() => insertTag("Stock")} className="btn btn-secondary m-1 flex-1 py-2">Stock</button>
                                                   
                                                    </div>
