                                                        <div class="form-group">
                                                            <label for="exampleformcontrolinputtext1" class="form-label">Add Template</label>
                                                            <div class="form-control-wrap">
                                                                <textarea
                                                                    cols="30"
                                                                    rows="10"
                                                                    class="form-control"
                                                                    id="exampleformcontrolinputtext1"
                                                                    placeholder="Enter Body"
                                                                    name="heading"
                                                                    value={heading}
                                                                    onChange={(e) => setheading(e.target.value)}
                                                                    ref={textarearef}
                                                                ></textarea>
                                                            </div>
                                                        </div>
                                                    </div>
                                                    <div class="col-md-12">
                                                        <div class="form-group" style="marginTop: 2%">
                                                            <div class="form-control-wrap">
                                                                <button class="btn" style="background: #E2545E; color: white" onClick={handlesubmit}>Save</button>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                        <div class="card shadow-none col-md-4">
                                            <div class="card-body">
                                                <div class="row">
                                                    <div class="col-md-12" style="border: 1px solid gray; padding: 15px; borderRadius: 5px; overflow: hidden">
                                                        <div class="form-group" style="marginTop: 2%">
                                                            <div class="form-control-wrap">
                                                                <p>{templatename}</p>
                                                                <h3>{greetings}</h3>
                                                                <p>{heading}</p>
                                                                <b><p>{footer}</p></b>
                                                                <p></p>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

