import React, { useState } from 'react';
import Helpers from '../../Config/Helpers';
import Sidebar from '../../Components/Sidebar';

const Store = () => {
    const [name, setName] = useState('');

    const handleSubmit = async () => {
        if (!name.trim()) {
            Helpers.toast("error", "Please enter a store name");
            return;
        }
        const baseUrl = Helpers.apiUrl;
        const token = localStorage.getItem("token");
        const response = await fetch(`${baseUrl}add-store`, {
            method: 'POST',
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
                Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify({ name })
        });
        if (response.status === 200) {
            const data = await response.json();
            console.log(data);
            Helpers.toast('success', "Store Saved Successfully!");
            setName("");
        }
        else {
            Helpers.toast("error", "Error saving store. Please try again later.");
        }
    };

    return (
        <div className="flex justify-center items-center h-screen bg-[#ebe9e9]">
            <Sidebar />
            <div
                className="container bg-white p-8 rounded-lg shadow-lg"
                style={{
                    maxWidth: "400px"
                }}
            >
                <h2 className="text-2xl font-bold mb-4">Create Store</h2>
                <input
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    placeholder="Store Name"
                    className="w-full border rounded-md px-4 py-4 mb-4"
                />
                <button
                    onClick={handleSubmit}
                    className="w-full bg-[#E7737B] text-gray-200 font-bold py-2 px-4 rounded-md hover:bg-[#d4959a]"
                >
                    Submit
                </button>
            </div>
        </div>
    );
};

export default Store;
